import React from 'react';
import group from '../photos/group.jpg'
import styled from 'styled-components'
import { FaHeart, FaGlobeEurope, FaRocketchat } from "react-icons/fa";


const Div = styled.div`
margin-top: 100px;
padding-top: 60px;
width:100vw;
height:100vh;
background-color: green;
background-image: url(${group});
background-position:center;
background-repeat:no-repeat;
background-size:cover;
background-attachment: fixed;
background-blend-mode: multiply;
@media (max-width: 768px) {
    height:150vh;
  }
`

const H1 = styled.h1`
color: #fefe;
text-align:center;
font-size: 20px;
`

const Wrapper = styled.div`
background: rgb(247, 247, 247, .8);
text-align:center;
padding:20px;
margin-top: 180px;
color: #3d3d3d;
@media (max-width: 768px) {
    margin-top: 20px;
  }
`

const Item = styled.div`
    box-sizing:border-box;
    display:inline-block;
    margin:30px;
    width:30%;
    @media (max-width: 768px) {
    width: 80%;
    font-size:10px;
  }
`


const userGradient = {
    background: "linear-gradient(to right, #f12711, #f5af19)",
    WebkitBackgroundClip: "text", // Removed !important
    backgroundClip: "text", // Removed !important
    color: "white"
};


function Advantages() {
    return (
        <Div >
            <H1>Genom oss har 42 000 människor hittat sin perfekta partner.
                Försök, det är gratis att gå med.</H1>
            <Wrapper>
                <Item>
                    <FaHeart size={70} style={userGradient} />
                    <h2>Mer passion i ditt liv</h2>
                    <h3>Vi blir alla uttråkade nu och då; vi behöver nya utmaningar som hjälper oss att bryta oss loss från våra vanliga rutiner.</h3>
                </Item>
                <Item>
                    <FaGlobeEurope size={70} />
                    <h2>Anonymt flirtande</h2>
                    <h3>De flesta gillar att flirta, men att bli igenkänd på en hemsida kan vara ett stort steg. På detta sätt kan du börja med en trygg känsla!</h3>
                </Item>
                <Item>
                    <FaRocketchat size={70} />
                    <h2>Hur du kommer igång!</h2>
                    <h3>Skapa din profil på mindre än en minut! Med en profil kan du kommunicera med andra medlemmar.</h3>
                </Item>
            </Wrapper>
        </Div>
    );
}

export default Advantages;
