import React from 'react';
import couple from '../photos/couple.jpg'
import styled from 'styled-components'
import {
    BrowserView,
    MobileView,
    isBrowser,
    isMobile
} from "react-device-detect";

const Img = styled.div`
width:100vw;
height:90vh;
background: url(${couple});
background-position:center;
background-repeat:no-repeat;
background-size:cover;
`

const Div = styled.div`
width:70%;
height:90vh;
background:#c75850;
opacity: 0.7;
clip-path: polygon(0 0, 61% 0, 100% 100%, 0% 100%);
display: grid;
  justify-content: center;
  align-items: center;
`

const Button = styled.button`
border:none;
width: 80%;
background: white;
padding: 20px 0px;
cursor: pointer;
border-radius: 5px;
box-shadow: 0px 10px 10px -10px #fefefe;
`


const Heading = styled.h1`
display:block;
color: white;
`

function Hero() {
    console.log(isBrowser)
    return (
        <Img >
            <Div>
                <Heading>Skaffa nya v√§nner...</Heading>
                <a>
                    <Button>KLIKNI ME JEBO TI MATER</Button>

                </a>
            </Div>
        </Img>
    );
}

export default Hero;
