import React from 'react';
import couple from '../photos/couple.jpg'
import styled from 'styled-components'
import { isBrowser } from "react-device-detect";

const Img = styled.div`
width:100vw;
height:70vh;
background: url(${couple});
background-position:center;
background-repeat:no-repeat;
background-size:cover;
`

const Div = styled.div`
width:50%;
height:70vh;
background: white;
opacity: .9;
display: grid;
  justify-content: center;
  align-items: center;
`

const Button = styled.button`
border:none;
width: 200px;
background-image: linear-gradient(to right, #f12711, #f5af19);
padding: 20px 0px;
cursor: pointer;
border-radius: 5px;
box-shadow: 0px 10px 30px -10px #f12711;
color: white;
position:absolute;
left: 30%;
top: 50%;

@media (max-width: 768px) {
    left: 20%;
  }
`


const Heading = styled.h3`
display:block;
color: white;
position:absolute;
left: 30%;
top: 45%;
background: linear-gradient(to right, #f12711, #f5af19);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`

function Hero() {
  console.log(isBrowser)
  return (
    <Img >
      <Div>
        <Heading>Klicka och börja äventyr</Heading>
      </Div>
      <a href={isBrowser ? "https://trackoptimise.com/?a=10438&c=65781&s1=006" : "https://trackoptimise.com/?a=10438&c=65750&s1=006"}>
        <Button>GO TO SITE</Button>
      </a>
    </Img>
  );
}

export default Hero;
