import React from 'react';
import couple from '../photos/couple.jpg'
import styled from 'styled-components'
import { isBrowser } from "react-device-detect";

const Img = styled.div`
width:100vw;
height:90vh;
background: url(${couple});
background-position:center;
background-repeat:no-repeat;
background-size:cover;
`

const Div = styled.div`
width:70%;
height:90vh;
background: white;
opacity: .8;
clip-path: polygon(0 0, 61% 0, 100% 100%, 0% 100%);
display: grid;
  justify-content: center;
  align-items: center;
`

const Button = styled.button`
border:none;
width: 200px;
background-image: linear-gradient(to right, #f12711, #f5af19);
padding: 20px 0px;
cursor: pointer;
border-radius: 5px;
box-shadow: 0px 10px 10px -10px #fefefe;
color: white;
position:absolute;
left: 10%;
top: 60%;
`


const Heading = styled.h1`
display:block;
color: white;
`

function Hero() {
    console.log(isBrowser)
    return (
        <Img >
            <Div>
                <Heading>Skaffa nya v√§nner...</Heading>
                <a href={isBrowser ? "https://www.facebook.com/home.php#_=_" : "https://www.youtube.com/watch?v=aOZyxwuhnk8"}>
                    <Button>KLIKNI ME JEBO TI MATER</Button>
                </a>
            </Div>
        </Img>
    );
}

export default Hero;
